INSERT INTO `sss` (`id`, `adi`, `sira`, `aciklama`, `durum`) VALUES
(16, 'Yazılımı Nasıl Satın Alabilirim?', '1', '<p><strong>Satın Al</strong> butonuna tıkladığınızda sizi kayıt olmaya&nbsp;y&ouml;nlendiricektir. Kayıt oldukdan sonra online &ouml;deme ile &ouml;demenizi tamamlayın. Ardından hesabım b&ouml;l&uuml;m&uuml;nden <strong>temalarım </strong>alanından indirebilirsiniz.</p>\r\n\r\n<p>Aksi Bir Durumda <strong>Whatsappdan </strong>İletişime Ge&ccedil;in.</p>\r\n', '0'),
(17, 'Yazılımı Farklı Alan Adlarında Kullanabilir miyim?', '2', '<p>Evet, istediğiniz kadar alan adında kullanabilirsiniz.</p>\r\n', '0'),
(18, 'Kurulum İşleminde Yardım Edicek Misiniz?', '3', '<p>Evet, satın almadan &ouml;nce yazılımı sizin kurmanızı istiyorum, demeniz yeterlidir.</p>\r\n', '0'),
(19, 'Sitenin İçeriklerini Nasıl Güncelleyebilirim?', '4', '<p>Sitemizin kolay y&ouml;netim paneli vardır. Oradan sitedeki her alanı değiştirebilir veya g&uuml;ncelleyebilirsiniz. Bunun i&ccedil;inde <strong>Admin Panelini</strong> inceleyiniz.</p>\r\n', '0'),
(20, 'Kredi kartı ile nasıl ödeme yapılır? (Taksitli)', '5', '<p>Kredi kartı ile satın almak istediğinizde &uuml;r&uuml;n&uuml;n siparişini kredi kartı &ouml;deme y&ouml;ntemini se&ccedil;erek tamamlayınız. Daha sonra &ccedil;ıkan ekranda taksitli veya tek &ccedil;ekim kredi kartı &ouml;demesi yapmak istediğinizi bildiriniz.</p>\r\n', '0'),
(21, 'Daha önce sitem olmadı? Kullanabilir miyim?', '6', '<p>UT Web Tasarım&nbsp;olarak tarafımızdan alacağınız t&uuml;m yazılım ve sistemlerde tam kontrol sağlayan bir y&ouml;netim paneli mevcuttur, bu y&ouml;netim paneli ile ister telefonunuzdan, ister bilgisayarınızdan t&uuml;m ayarları ve i&ccedil;erik girişini kolayca yapabilirsiniz. Kontrol paneli, sitenizi hi&ccedil; bir teknik bilgi olmadan herkes i&ccedil;in kolayca y&ouml;netilebilir olması i&ccedil;in tasarlanmıştır. Yinede herhangi bir sorun yaşadığınızda destek bildirimi oluşturmanız durumunda hemen m&uuml;dahale edebiliriz.</p>\r\n', '0'),
(22, 'Ürünlerin iade hakkı var mıdır?', '7', '<p>Yazılımlarımı a&ccedil;ık kaynak olması sebebi ile herhangi bir iade s&ouml;z konusu değildir. Bu sebeple &uuml;r&uuml;n&uuml; satın almadan &ouml;nce bir demo site kurulup t&uuml;m detayları incelemeniz ve t&uuml;m isteklerinize cevap verdiğini anladığınızda satın almanız gerekir.</p>\r\n', '0'),
(23, 'Web site kurulduktan sonra içerik nasıl girilir?', '8', '<p>Web site kurulumu tamamlandıktan sonra genelde t&uuml;m m&uuml;şterilerimizin i&ccedil;eriklerini edit&ouml;rlerimiz tarafından girmekteyiz. Bu satın alma aşamasında anlaşmaya bağlı olarak değişkenlik g&ouml;sterebilir. Eğer siz giriş yapmak isterseniz tarafınıza verilen admin bilgilerinizi kullanarak gelişmiş panel yardımı ile kolayca girebilirsiniz.</p>\r\n', '0'),
(24, 'Satın alınmış yazılımlarda sorunları nasıl halledebilirim?', '9', '<p>Sistemden kaynaklı her t&uuml;rl&uuml; sorunu teknik ekibimiz halledecektir. Sistem kaynaklı yaşadığınız sorunu detaylı şekilde anlatımını m&uuml;şteri panelinizde bulunan destek talebi ile bize ulaştırabilirsiniz. (Sorun paylaşımı yapıldığında sitenizin FTP ve Admin panel bilgilerini g&ouml;ndermeniz hızlı &ccedil;&ouml;z&uuml;me ulaşmak i&ccedil;in &ouml;nemlidir.)</p>\r\n', '0'),
(25, 'Cpanelden Kurulum Videosu', '01', '<p><iframe frameborder=\"0\" height=\"213\" src=\"https://www.youtube.com/embed/XxrnEYGTDDY\" title=\"YouTube video player\" width=\"100%\"></iframe></p>\r\n', '0');
